INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (869, 'Leader Technique Java/J2EE at Voyages-sncf.com (Paris, France)', '&lt;p&gt;&lt;strong&gt;Contexte et p&amp;eacute;rim&amp;egrave;tre&amp;nbsp;:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Au sein de notre &amp;eacute;quipe ATLAS, le domaine m&amp;eacute;tier &amp;agrave; aborder concerne la distribution et le calcul d&amp;rsquo;itin&amp;eacute;raires ferroviaires en France et en Europe. L&amp;rsquo;&amp;eacute;quipe intervient sur des applications clefs de la SNCF et de Voyages-Sncf dont elle assure &amp;agrave; la fois les &amp;eacute;volutions comme la maintenance.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Les applications &amp;agrave; g&amp;eacute;rer sont distribu&amp;eacute;es en mode saas et sont principalement expos&amp;eacute;es via webservices.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Bien que des fronts IHM web soient r&amp;eacute;guli&amp;egrave;rement r&amp;eacute;alis&amp;eacute;s, les principaux d&amp;eacute;veloppements sont faits c&amp;ocirc;t&amp;eacute; Backend.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Ce que nous attendons de vous&amp;nbsp;? &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Int&amp;eacute;gr&amp;eacute; au sein de l&amp;rsquo;&amp;eacute;quipe, vous avez pour mission principales de participez aux d&amp;eacute;veloppements, mais pas seulement. Vous &amp;ecirc;tes &amp;eacute;galement garant des t&amp;acirc;ches suivantes&amp;nbsp;:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Application des bonnes pratiques de d&amp;eacute;veloppement logiciel (Software Craftsmanship)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Veille au respect des engagements de qualit&amp;eacute; de code&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Accompagnement du Product Owner et du ScrumMaster dans les sp&amp;eacute;cifications techniques et dans les chiffrages/estimation&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Suivi de la plateforme d&amp;rsquo;int&amp;eacute;gration continue&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Accompagnement et passage de comp&amp;eacute;tences aux autres membres de l&amp;rsquo;&amp;eacute;quipe, Pair-Programming&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Veille technologique et m&amp;eacute;thodologique.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Paris, France', 'http://careers.stackoverflow.com/jobs/99796/leader-technique-java-j2ee-voyages-sncfcom', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 869);
