INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (879, 'Senior iOS Developer at Smartsheet (Bellevue, WA)', '&lt;p&gt;Smartsheet is seeking a full time Senior Mobile Software Engineer to join our Development team. In this position you will design and build the next generation of the mobile versions of Smartsheet&amp;rsquo;s collaborative work and project management software. Our mobile software team is small, highly efficient, and results oriented. We work in an agile collaborative environment that stays focused on building great software. Here you can use the latest mobile and web technologies to their fullest potential. We are looking for the most highly motivated and intelligent individuals. This position is based at our headquarters in Bellevue, WA. Job responsibilities include:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Participate in the entire development life cycle, from concept to release&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work closely with product managers, UI designers, and testers in a highly collaborative team environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Deliver secure, high performance, maintainable code using best practices&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Bellevue, WA', 'http://careers.stackoverflow.com/jobs/90161/senior-ios-developer-smartsheet', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 863);
