INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (880, 'Junior Frontend Developer (f/m) at LLS Internet GmbH (Berlin, Deutschland)', '&lt;p&gt;&lt;strong&gt;What we offer&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;You will become part of a highly professional and motivated team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You gain the unique opportunity to experience and leave your mark on the growth of a recently founded company&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will work in an ambitious environment with a strong focus on personal growth and open communication, in which you can contribute your own ideas&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will have a free choice of operating system for development&lt;/li&gt;&lt;br /&gt;&lt;li&gt;We provide licenses for your favorite Editor/IDE (PhpStorm, Sublime etc.)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;We are open-minded about new technologies and use them wherever it makes sense&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Nice perks including free coffee and drinks, fresh fruits and regular team events&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Your Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Writing automated unit and End-to-End tests will be crucial part of your workflow&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaboration with designers will happen using an automated style guide&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Nothing is made in stone, so you will make day to day decisions in terms of application architecture and technology stack&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Improving your skills by doing constant code review&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Gaining tremendous insight into advanced JavaScript (AngularJS)&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Deutschland', 'http://careers.stackoverflow.com/jobs/99794/junior-frontend-developer-f-m-lls-internet-gmbh', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 880);
