INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (882, 'Lead Software Engineer at ikaSystems (Southborough, MA)', '&lt;p&gt;ikaSystems is seeking a Lead/Manager, Development Software Engineering to develop components for the ikaEnterprise platform.&amp;nbsp; .Net developer (C#/ASP.NET), Oracle skills preferred.&amp;nbsp; Consideration will also be given to strong developers who have substantial programming and technical experience in technologies other than .Net and Oracle, but who have specific industry knowledge pertinent to healthcare and health insurance.&amp;nbsp; May also lead and mentor others.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Essential Job Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Significant hands-on development experience using Microsoft .Net Framework (through 3.5/4.0) with C# and ASP.NET (through 3.5/4.0)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Development of Web Services using WCF&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Strong knowledge of ASP.NET Page life cycle, user controls, custom controls, and ASP.Net AJAX&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Strong knowledge of OOAD and multi-tier architectures, including MVC/MVP/MVVM design patterns&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Strong knowledge of JQuery, JavaScript, and CSS&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Strong experience with RDBMS (Oracle 10g/11g, MS SQL 2005/2008), including database design, developing stored procedures and functions, and performance tuning&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Demonstrated expertise tuning web applications from top to bottom&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Strong working experience with Microsoft IIS (6.0 and 7.0)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in project planning sessions, working closely with business analysts and team members to analyze requirements and provides design recommendations for complex systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Design and develop new software or make modifications to existing complex software applications in accordance with written specifications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop software using disciplined processes, adhering to industry standards and best practices, including complete ease with agile development methods throughout the SDLC&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Demonstrate software to all stakeholders&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Share knowledge and provide technical guidance to other team members within area of expertise&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Take part in reviews of work, e.g. design and code reviews&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience supporting enterprise level software infrastructure for external clients&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with technical staff to analyze and understand problems with complex software systems and resolve them&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Resolve customer complaints and respond with suggestions for improvements and enhancements&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Minimum Education/Experience Requirements: &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Bachelor''s or Master''s degree in Software/Computer Engineering, Computer/Information Sciences or Mathematics (willing to accept foreign education equivalent) as well as 5-7 years&amp;rsquo; experience in a similar role.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Job Location: &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Southborough, Massachusetts, United States&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Position Type: &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Full-Time/Regular&lt;/p&gt;&lt;br /&gt;&lt;p&gt;ikaSystems plays a leading role in the exciting transformation of the healthcare industry by delivering advanced business technology to health plans within the commercial, Medicare, Medicaid, Exchange, and ACO business. Our cutting-edge business software and outstanding customer service enables plans to thrive in a quickly changing, competitive healthcare landscape.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As an ikaSystems employee, creativity and innovation will be encouraged, and we will help you to grow professionally and move toward your career goals. When you join ikaSystems, you join a visionary company set for continuing success.&amp;nbsp; We offer a competitive compensation and benefits package including Medical, Dental, Life &amp;amp; Disability, paid time off, and 401k.&lt;/p&gt;', 'Southborough, MA', 'http://careers.stackoverflow.com/jobs/99245/lead-software-engineer-ikasystems', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 882);
