INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (884, 'Software Developer (with Saleforce) at Denplan (Winchester, UK)', '&lt;p&gt;As a Senior Salesforce Developer you should strive to find simple solutions to complex problems and care not only about the efficiency and maintainability of software but also about the way it helps other teams do their jobs.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will work with a team of great people to solve problems creatively, using the latest technologies to deliver software solutions that will help make Denplan standout in a competitive market place.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Specializing in Salesforce development, you will provide the technical expertise to drive the strategic development of our new Salesforce instance and its integration with our other core systems.&lt;/p&gt;', 'Winchester, UK', 'http://careers.stackoverflow.com/jobs/99793/software-developer-with-saleforce-denplan', 'Wed, 07 Oct 2015 17:02:02 Z', '2015-10-07T17:02:02Z', 884);
