INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (891, 'Software Developer, Mid-Level at Naxos of America (Franklin, TN)', '&lt;p&gt;Naxos of America is hiring a mid-level software developer in Franklin TN. This position will be an integral part of the team that is implementing the next generation of our infrastructure, including core pieces of our digital music delivery platform (think iTunes, Spotify, Pandora, etc.). You&amp;rsquo;ll have the opportunity to work on any and all aspects of an integrated DevOps team, concentrating on full-stack web development, currently on the .NET platform and incorporating Javascript, C#, and Sql Server. Our teams operate in Scrum with short sprints, and projects are generally fast paced without being frantic.&lt;/p&gt;', 'Franklin, TN', 'http://careers.stackoverflow.com/jobs/99791/software-developer-mid-level-naxos-of-america', 'Wed, 07 Oct 2015 16:02:02 Z', '2015-10-07T16:02:02Z', 891);
