INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (894, 'Software Engineer at Manifold Technology (San Jose, CA)', '&lt;p&gt;Manifold is building a platform to manage cryptographic currencies and protocols. &amp;nbsp;We are seeking a software engineer with expertise in server-side development, building concurrent services, distributed system development, the concepts and tools necessary to interface with cryptographic currency protocols, and scalable production operations. &amp;nbsp;You&amp;rsquo;ll be part of a team building a robust platform that will integrate with cryptographic currencies and financial infrastructure.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You are energetic, intellectually curious, and enjoy tough engineering problems. &amp;nbsp;If you want to be challenged, grow technically, and learn how to secure infrastructure against the most advanced adversaries, you&amp;rsquo;ll enjoy this role.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Here&amp;rsquo;s what you''ll do:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop, integrate and implement related system components, server-side applications and database integration modules&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write high-quality code with emphasis on security, performance and scalability&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Prepare technical documentation&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work within an Agile methodology to predict and deliver improvements to the platform&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with Product Management, Integration Engineers, and QA&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'San Jose, CA', 'http://careers.stackoverflow.com/jobs/99790/software-engineer-manifold-technology', 'Wed, 07 Oct 2015 16:02:02 Z', '2015-10-07T16:02:02Z', 894);
