INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (899, 'QA Engineer at On-Site (Campbell, CA)', '&lt;p&gt;We are looking for a Quality Assurance Engineer who can support a small team of engineers and develop automated tests that we can rely on to ensure our new code is safe to release.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Do you take joy in spotting what others miss? We are looking for someone who understands how frustrating it is when things don&amp;rsquo;t work right; who feels it is their duty to protect the world from bad code.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We want an engineer who understands how to build automated tests that are reliable and easy to maintain.&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Support a team of engineers, acting as gatekeeper for their changes&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build and maintain automated test suites for multiple web based products&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Identify areas of risk and develop appropriate test plans&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Perform investigation of escalated issues&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&amp;ldquo;Fight for the user&amp;rdquo;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Campbell, CA', 'http://careers.stackoverflow.com/jobs/89613/qa-engineer-on-site', 'Wed, 07 Oct 2015 16:02:02 Z', '2015-10-07T16:02:02Z', 899);
