INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (903, 'Software Programmer at Advanced Business Computers of America, Inc. (ABCoA) (Jacksonville, FL)', '&lt;p&gt;.NET Software Developer&lt;/p&gt;&lt;br /&gt;&lt;p&gt;At Advanced Business Computers of America, Inc. (ABCoA) our mission is to provide the premier software solutions to the global subprime industry. We achieve our mission by developing customer-centric software that simplifies the daily activities of automotive and finance professionals worldwide.&amp;nbsp; We are currently seeking a Software Engineer, to join our team of bright, innovative designers.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If you are excited about using the newest technologies to build on established programs and create new designs we want to talk to you!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Our Company offers full benefits, matching 401K, and world-class working facilities.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;9am-6pm EST, M-F, on site&lt;/p&gt;', 'Jacksonville, FL', 'http://careers.stackoverflow.com/jobs/79597/software-programmer-advanced-business-computers', 'Wed, 07 Oct 2015 16:02:02 Z', '2015-10-07T16:02:02Z', 903);
