INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (921, 'Test Automation Engineer at Centralway (Zurich, Switzerland)', '&lt;p&gt;&lt;strong&gt;About Centralway&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Centralway&amp;rsquo;s focus is on developing products to manage banking transactions and assets. The platform provides banking services for the consumer, as well as the development and implementation of banking software towards financial institutions. We are currently looking for a Test Automation Engineer, at the earliest possible start date, to strengthen our position in the industry.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This role is based at Centralway Headquarters, Z&amp;uuml;rich.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As Test Automation Engineer, you will develop automated test sequences in order to ensure the highest level of app quality. Working closely with the development team, you will create strategies for automated test scenarios, supporting the planning of product releases.&lt;br&gt;&lt;br&gt;In this position, you will be responsible for assisting development teams with regular code reviews, so as to reach the high quality standards of Centralway.&lt;br&gt;&lt;br&gt;In addition, you will evaluate innovative technologies and produce risk assessments with regards to code and architecture implementation.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Requirements&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;B.S. or M.S. degree in computer science or equivalent work experience relevant to the role&lt;/li&gt;&lt;br /&gt;&lt;li&gt;At least 2 years of experience in a comparable position&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Solid knowledge in the field of automated testing on iOS and Android platforms with XCTest and Espresso / Junit or comparable test environments&lt;/li&gt;&lt;br /&gt;&lt;li&gt;High level of software expertise in system analysis and implementing test cases&lt;/li&gt;&lt;br /&gt;&lt;li&gt;An ISTQB Certification (CTFL) is a plus&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Excellent communication skills in English, German is an advantage&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Our Office&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Centralway expects passion and commitment from our employees in order to reach our goals. Our unique working environment has been tailored to this purpose:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Caf&amp;eacute; and restaurant&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Air-conditioned cinema&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Free breakfast and lunch buffets, snacks and ice-cold drinks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Sports club&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Fitness studio&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Many extra perks and benefits&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Zurich, Switzerland', 'http://careers.stackoverflow.com/jobs/99785/test-automation-engineer-centralway', 'Wed, 07 Oct 2015 16:02:02 Z', '2015-10-07T16:02:02Z', 921);
