INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (926, 'Systems Engineer at Centralway (Zurich, Switzerland)', '&lt;p&gt;&lt;strong&gt;&amp;Uuml;ber Centralway&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Centralway hat das Ziel die weltweit f&amp;uuml;hrende Mobile Banking Platform zu entwickeln. Die Banking App Centralway Numbrs ersetzt dabei die traditionelle Bankfiliale. Das Produkt erm&amp;ouml;glicht den Nutzern eine einfache und sichere Handhabung der Finanzen. Um die Marktposition von Centralway weiter zu st&amp;auml;rken suchen wir zum baldm&amp;ouml;glichsten Zeitpunkt einen Systems Engineer (m/w).&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Ihr Arbeitsort ist Z&amp;uuml;rich.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Aufgaben&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Sie werden Teil eines Teams aus Entwicklern und Systemadministratoren sein, die verantwortlich sind f&amp;uuml;r die Systeme, Anwendungen und die Infrastruktur des gesamten Unternehmens. Dies beinhaltet z.B. Systeme wie AWS, Firewalls, VPN, Confluence, Splunk, Jenkins und weitere Systeme.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Dar&amp;uuml;ber hinaus, pflegen Sie die bereits bestehende Infrastruktur und entwickeln die entsprechenden Systemkomponenten einer neuen Infrastruktur.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Unter Anwendung Ihrer Entwicklungskompetenz, werden Sie bei der Gestaltung neuer Applikationsbausteine und/oder Tools involviert sein.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Zus&amp;auml;tzlich zu dem End-to-End Design, werden die Entwicklung und die Wartung von kleineren Ad-Hoc Tools notwendig sein. In einer engen Zusammenarbeit mit unseren Entwicklern stellen Sie sicher, dass die angewendeten Applikationskomponenten mit der Produktionsumgebung kompatibel und f&amp;uuml;r diese optimiert sind.&lt;br&gt;&lt;br&gt;Arbeitszeit: Vollzeit&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Qualifikation&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Abgeschlossenes Studium der Informatik oder eine gleichwertige relevante Berufserfahrung&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Ausgezeichnetes, konzeptionelles Verst&amp;auml;ndnis gekoppelt mit einem tiefgr&amp;uuml;ndigen Wissen &amp;uuml;ber Anwendungsarchitekturen - komfortable Gestaltung einer hochqualifizierten L&amp;ouml;sungsarchitektur bis hin zu ihrer Entwicklung&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maximal 5 Jahre Erfahrung in der Anwendungsentwicklung und/oder System-Design&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Sehr gutes Verst&amp;auml;ndnis f&amp;uuml;r Backend Entwicklungstechniken wie automatisierte Entwicklung, Unit-Tests, Datenbankenmigrationen und Performance Tuning&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Sicherer Umgang mit einer cloudbasierten Umgebung und Konzepten wie Auto-Scaling&lt;/li&gt;&lt;br /&gt;&lt;li&gt;F&amp;auml;higkeit leistungsf&amp;auml;higen und wiederverwendbaren Code f&amp;uuml;r Tools und Infrastruktur-Komponenten zu schreiben&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Sehr gute Sprachkenntnisse in Englisch, Deutschkenntnisse sind von Vorteil&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Unser Arbeitsort&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Bei Centralway arbeiten wir hart und leidenschaftlich. Um unsere gemeinsamen Ziele zu erreichen, haben wir eine einzigartige Arbeitsumgebung geschaffen. Unseren Mitarbeitern bieten wir:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Caf&amp;eacute; und Restaurant&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Eigenes klimatisiertes Kino&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Kostenloses Fr&amp;uuml;hst&amp;uuml;cksbuffet, Mittagsbuffet, Getr&amp;auml;nke und Snacks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Einen eigenen Sportverein&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Fitness Studio&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&amp;hellip; und viele weitere Extras&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Zurich, Switzerland', 'http://careers.stackoverflow.com/jobs/99782/systems-engineer-centralway', 'Wed, 07 Oct 2015 15:02:02 Z', '2015-10-07T15:02:02Z', 921);
