INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (937, 'Senior Frontend Developer at glispa GmbH (Berlin, Germany)', '&lt;p&gt;As a &lt;strong&gt;Senior Frontend Developer&lt;/strong&gt; you will be responsible for extending and optimizing glispa''s user interfaces, such as our internal campaign management and reporting tools or the publisher interface. You will work in a team of developers and designers to build cutting edge, graphical appealing user interfaces that provide a great user experience.&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Improve, extend, and optimize the companies internal and external user interfaces, such as the companies intranet, publisher interface, etc.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Use the latest Javascript/HTML/CSS Technologies to enhance user experience&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Work with Creative Designers and UX specialists to make interfaces appealing, yet practical&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Responsibility for parts of the project to ensure timely delivery&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/97219/senior-frontend-developer-glispa-gmbh', 'Wed, 07 Oct 2015 15:02:02 Z', '2015-10-07T15:02:02Z', 937);
