INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (940, 'Developer at Bank of Canada (Ottawa, ON, Canada)', '&lt;p&gt;Two Positions - Findur Developer &amp;amp; Developer, Banking System and Financial Markets Applications&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Application deadline: 21 October 2015&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Findur Developer&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As an Application Developer centred around Findur, you participate in the design, installation, development, modification, maintenance and enhancement of business applications. The incumbent collaborates with clients and colleagues to identify user requirements, assesses available technologies and recommends solution options.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Central Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;recommend technical solutions to achieve business needs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;design and develop business application systems and ensure compliance with standards and established methodologies and practices&lt;/li&gt;&lt;br /&gt;&lt;li&gt;participate in implementation activities, e.g., set up environment and conduct system/acceptance tests&lt;/li&gt;&lt;br /&gt;&lt;li&gt;analyze, design and implement enhancements to existing applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;provide programming expertise in the analysis, design and development of business applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;maintain collaborative relations with internal clients and external counterparts to support operational objectives&lt;/li&gt;&lt;br /&gt;&lt;li&gt;provide support, including on-call, shift and/or weekend work as required, for systems within area of expertise&lt;/li&gt;&lt;br /&gt;&lt;li&gt;participate in Type 1 &amp;ndash; 4 projects and provide advice related to field of expertise&lt;/li&gt;&lt;br /&gt;&lt;li&gt;evaluate IT products and potential solutions to support the evolution of the environment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;identify and manage technology-specific standards relating to programming&lt;/li&gt;&lt;br /&gt;&lt;li&gt;provide on-the-job training to colleagues&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Developer, Banking System and Financial Markets Applications&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The Bank of Canada&amp;rsquo;s Information Technology Services Department is seeking experienced Java developers to join the Application Service team within the Banking Operations and Financial Markets Portfolio.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As an application developer, you participate in the design, installation, development, modification, maintenance and enhancement of business applications. Under the general direction of the Team Lead, you will support the applications used for Banking System and Financial Markets, as well as provide on-call support. You will collaborate with clients and colleagues to identify user requirements, assess available technologies and recommend options for solutions.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Central Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;develop JEE application changes and modifications to the Banking System and Financial Markets suite of applications&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;participate in the code review process&lt;/li&gt;&lt;br /&gt;&lt;li&gt;provide on-call support on a rotating basis&lt;/li&gt;&lt;br /&gt;&lt;li&gt;discuss client requests directly with the client&lt;/li&gt;&lt;br /&gt;&lt;li&gt;work with other team members to provide high-quality solutions in a timely fashion&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;make suggestions and provide feedback to processes and architecture as part of a continuous-improvement model&lt;/li&gt;&lt;br /&gt;&lt;li&gt;analyze problems and provide multiple solutions for lead developer and client feedback&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Ottawa, ON, Canada', 'http://careers.stackoverflow.com/jobs/99776/developer-bank-of-canada', 'Wed, 07 Oct 2015 15:02:02 Z', '2015-10-07T15:02:02Z', 516);
