INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (950, 'Senior Backend Engineer at Centralway (Zurich, Switzerland)', '&lt;p&gt;&lt;strong&gt;About Centralway&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Centralway&amp;rsquo;s focus is on developing products to manage banking transactions and assets. The platform provides banking services for the consumer, as well as the development and implementation of banking software towards financial institutions. We are currently looking for a Senior Backend Engineer, at the earliest possible start date, to strengthen our position in the industry.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This role is based at Centralway Headquarters, Z&amp;uuml;rich.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Your responsibilities will include the continuous development and improvement of the current backend and service architecture of Centralway Numbrs.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will administer and support our backend API, databases and other components. Researching and remaining up-to-date with the latest technologies will help you to implement scalability and redundancy in all parts of the application.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;In close cooperation with the various departments of Centralway, you will continuously develop and improve the backend structure, based on the product vision.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;On top of your operational responsibilities, you will also be required to coach and mentor junior technical talent.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Requirements&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;B.S. or M.S. degree in computer science or equivalent work experience relevant to the role&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Minimum of 5 years of experience as a software developer, preferably in backend development&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Solid knowledge of, and experience in, modern object-oriented programming languages, such as Golang, Java, C++, or other&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Comfortable with DevOps tasks and deployment tools such as Ansible, Puppet or Chef&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Knowledge of at least one scripting language (Ruby, Python, Perl or other)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with Git and continuous integration, passion for benchmarking and optimisation&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with relational and No SQL Database&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with HBCI/FinTS or SWIFT is preferred&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Passion for benchmarking and optimisation with a preference for writing clean, elegant, test-driven code&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Excellent communication skills in English, German is an advantage&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Our Office&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Centralway expects passion and commitment from our employees in order to reach our goals. Our unique working environment has been tailored to this purpose:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Caf&amp;eacute; and restaurant&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Air-conditioned cinema&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Free breakfast and lunch buffets, snacks and ice-cold drinks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Sports club&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Fitness studio&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Many extra perks and benefits&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Zurich, Switzerland', 'http://careers.stackoverflow.com/jobs/99775/senior-backend-engineer-centralway', 'Wed, 07 Oct 2015 15:02:02 Z', '2015-10-07T15:02:02Z', 921);
