INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (954, 'Padawans PHP (Dveloppeurs) H/F at GLOBALIS media systems (Paris, France)', '&lt;p&gt;&lt;strong&gt;GLOBALIS recherche des D&amp;eacute;veloppeurs/ses PHP (Padawans PHP) talentueux/ses, polyvalentes et motiv&amp;eacute;es pour rejoindre la Force et relever ensemble de nombreux challenges.&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Si PHP est ton langage de pr&amp;eacute;dilection,&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Si tu aimes d&amp;eacute;velopper &amp;agrave; l&amp;rsquo;aide d&amp;rsquo;un framework (ZF, Symfony, CodeIgniter, Laravel&amp;hellip;) ou d&amp;rsquo;un CMS (Wordpress, Drupal&amp;hellip;),&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Si tu connais Javascript (jQuery, AngularJS, NodeJS&amp;hellip;), HTML, CSS&amp;hellip;,&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Si tu pratiques SQL (MySQL, MariaDB, PostgreSQL,&amp;hellip;),&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Si tu sais utiliser Linux en ligne de commande et commiter sous Git,&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;em&gt;&amp;minus; Les aptitudes techniques, pour nous rejoindre, probablement, tu poss&amp;egrave;des&amp;hellip;&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;Si tu sais &amp;eacute;couter, comprendre un besoin et le reformuler,&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Si tu adores r&amp;eacute;soudre des probl&amp;egrave;mes en trouvant des solutions &amp;eacute;l&amp;eacute;gantes et originales,&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Si tu aimes &amp;eacute;crire du code performant, clair, document&amp;eacute; et r&amp;eacute;utilisable,&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Si tu d&amp;eacute;sires apprendre de nouvelles choses, ma&amp;icirc;triser la force et partager ton savoir,&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Si tu fais aussi preuve d&amp;rsquo;humilit&amp;eacute; et que tu sais &amp;eacute;galement te remettre en question,&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;em&gt;&amp;minus; Les qualit&amp;eacute;s humaines, pour nous rejoindre, &amp;eacute;galement, tu as&amp;hellip;&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Alors, discutons-en ensemble !&lt;/strong&gt;&lt;/p&gt;', 'Paris, France', 'http://careers.stackoverflow.com/jobs/99774/padawans-php-d%C3%A9veloppeurs-h-f-globalis-media-systems', 'Wed, 07 Oct 2015 15:02:02 Z', '2015-10-07T15:02:02Z', 954);
