INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (958, 'Software Engineer at The Chronicle of Higher Education (Washington, DC)', '&lt;p&gt;The Chronicle of Higher Education seeks a talented&amp;nbsp;&lt;strong&gt;Software Engineer&lt;/strong&gt;&amp;nbsp;who is passionate about building our dynamic next-generation of web-based applications while evolving the current platform.&amp;nbsp; The Chronicle of Higher Education is building a formidable technology team in order to go far beyond its current digital content and services. The Software Engineer will work together with the team in creating and expanding multiple websites, extending and improving our object-oriented framework, building solutions and maintaining existing applications which will be highly visible across academe.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;The ideal candidate is self-motivated, creative, and excited about learning in order to develop solid solutions for our next generation web platform. The Software Engineer will collaborate with business owners to translate complex business initiatives into innovative solutions.&amp;nbsp;A can-do-attitude, with the ability to energize and work with cross-functional teams is required.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Primary Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Analyze and design software systems to form a basis for the solution of information processing problems.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop features across multiple subsystems within our applications, including collaboration in requirements definition, prototyping, design, coding, testing and deployment&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop software using Agile Development Methodology (SCRUM)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Investigate, analyze and make recommendations to management regarding technology improvements, upgrades and modifications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop software solutions by studying information needs; conferring with users; studying systems flow, data usage, and work processes; investigating problem areas; following the software development lifecycle.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Document and demonstrate solutions by developing documentation flowcharts, layouts, diagrams, charts, code comments and clear code.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Resolve production issues by implementing an effective permanent solution in order to prevent future issues&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Washington, DC', 'http://careers.stackoverflow.com/jobs/99771/software-engineer-the-chronicle-of-higher', 'Wed, 07 Oct 2015 15:02:02 Z', '2015-10-07T15:02:02Z', 958);
