INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (967, 'Software Developer at Sharp Notions, LLC (Fairport, NY)', '&lt;p&gt;&lt;strong&gt;&lt;strong&gt;About Us:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;If we had todescribe ourselves in one word, we probably couldnt choose. Were a lot of things. Were a software development and marketing design firm. Were a bunch of tech-savvy creators hungry for whatcomes next. Were in love with what we do and want you to be a part of it. Were Sharp Notions.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Position:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;We''re looking for a talented software developer to join our teamthat is responsible for delivering high-quality software solutions to our clients. Youwill be involved in all aspects of the ongoing software development from the initial specification, through to developing, testing and launching.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;ul&gt;	&lt;li&gt;	&lt;p&gt;Develop mobile applications, web services, and desktop applicationsfor our clients&lt;/p&gt;	&lt;/li&gt;	&lt;li&gt;	&lt;p&gt;Support front end development team by learning the ins and outs of our content management system and e-commerce frameworks and extending them as needed&lt;/p&gt;	&lt;/li&gt;	&lt;li&gt;	&lt;p&gt;Communicate with the design and project management teams to create the best solutions&lt;/p&gt;	&lt;/li&gt;	&lt;li&gt;	&lt;p&gt;Deliver work on time andwith attention to detail&lt;/p&gt;	&lt;/li&gt;	&lt;li&gt;	&lt;p&gt;Learn new skills and remain up-to-date with development best practices, industry trends, and emerging technologies&lt;/p&gt;	&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Requirements:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;ul&gt;	&lt;li&gt;	&lt;p&gt;Proficientin one of the following development languages: Java, C# .NET, or NodeJS.&lt;/p&gt;	&lt;/li&gt;	&lt;li&gt;	&lt;p&gt;Bonus points for JavaScriptand AngularJS&lt;/p&gt;	&lt;/li&gt;	&lt;li&gt;	&lt;p&gt;Ability to quickly pick up new languages, technologies, and frameworks&lt;/p&gt;	&lt;/li&gt;	&lt;li&gt;	&lt;p&gt;At least 2 years of relevant experience&lt;/p&gt;	&lt;/li&gt;	&lt;li&gt;	&lt;p&gt;Bachelors Degree preferred but not required. Equivalent experience is fine too.&lt;/p&gt;	&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Perks:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;Heres a peek at some of the many perks youll enjoy:&lt;/p&gt;&lt;ul&gt;	&lt;li&gt;A great work environmentits never boring here, trust us.&lt;/li&gt;	&lt;li&gt;Werea family - lunches for birthdays, holiday parties, team outings, movie days, etc.&lt;/li&gt;	&lt;li&gt;Collaborationideas and input are always welcome&lt;/li&gt;	&lt;li&gt;Casual dressno suit, no tie, no problem!&lt;/li&gt;	&lt;li&gt;Health and dental&lt;/li&gt;	&lt;li&gt;Paid vacation and holidays&lt;/li&gt;&lt;/ul&gt;', 'Fairport, NY', 'http://careers.stackoverflow.com/jobs/99768/software-developer-sharp-notions-llc', 'Wed, 07 Oct 2015 15:02:02 Z', '2015-10-07T15:02:02Z', 967);
