INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (973, 'ServiceNow Architect at The Boston Consulting Group (Chicago, IL)', '&lt;p&gt;The IT Operations &amp;ndash; ServiceNow Architect will own BCG IT&amp;rsquo;s ServiceNow platform enabling our IT as a Service operating model and Portfolio Management Office. The Architect will act as the ServiceNow Service Owner for BCG IT.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;BASIC JOB RESPONSIBILITIES:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Partner with Engineering, Operations and Product to ensure the roadmap is reflective of business needs and priorities&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Lead all Data Management, Security, Release Management, Product Updates / Relevance and Case Management services associated with SNC&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build and Manage ServiceNow Admin &amp;amp; Support team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Proficient in and is able to explain SNC existing functionality as well as future requirements including recommending the best architecture and design principles to meet user requirements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Visionary for product with responsibility for ensuring the roadmap is reflective of new features, upgrades, etc.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Primary point of contact with stakeholders on all risk, bottleneck and/or blockers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Analyze user requirements, identifies (alternate) systematic approaches, and develops recommendations with cross functional teams&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Lead meetings with key stakeholders, gathers and documents user requirements, and assists with change management for product enhancements&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Transform functional requirements into detailed technical designs that represent actionable development tasks&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Create business process flows and functional designs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop and deliver training programs relevant to Service Now use and administration for end users&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Primary point of contact with the vendor; drive all renewals, support, etc.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop and implement policies, procedures and guidelines to ensure data integrity&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Play a substantial role in driving break/fix analysis&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Assist with the testing, installation, and implementation of new / improved SNC systems&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Maintain user access and license inventory&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide daily customer service to all SNC users that submit questions and service requests to the SNC production support team&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/99766/servicenow-architect-the-boston-consulting-group', 'Wed, 07 Oct 2015 14:02:02 Z', '2015-10-07T14:02:02Z', 973);
