INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (985, 'Lead Android Developer (f/m) at DaWanda GmbH (Berlin, Deutschland)', '&lt;p&gt;&amp;nbsp;&lt;strong&gt;Your Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Build up and lead a team of Android Developers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Developing Apps for Android phones by collaborating with product managers, designers and user experience teams&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Specification of Restful APIs to connect our Apps with our backend (Ruby)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Write High Quality Code by Unit-, System- &amp;amp; Integrationtesting&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Help driving the architecture and technology choices that enable a world-class user experience&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Utilize various tools or frameworks to deliver rapid iterations of software features&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Raise the bar on quality, consistency, maintainability, efficiency and all the other things that make great Apps great&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Help define engineering best practices and provide technical mentorship to your team&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Deutschland', 'http://careers.stackoverflow.com/jobs/94349/lead-android-developer-f-m-dawanda-gmbh', 'Wed, 07 Oct 2015 14:02:02 Z', '2015-10-07T14:02:02Z', 426);
