INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (991, 'Senior Software Engineer (m/f) - Rocket Internet Company at Rocket Internet Company (Berlin, Germany)', '&lt;p&gt;As an Engineer at a Rocket company you help launching successful startup companies all over the world. Whatever technologies, languages, or development environments you&amp;rsquo;ve been using, we expect you have mastered them in depth.&amp;nbsp;&lt;br&gt;&lt;br&gt;If scale and speed of execution is exciting to you, if you are passionate about your craft and if you are striving to build systems that matter to millions of people around the world, one of our Rocket Companies will be the right place for you!&lt;br&gt;&lt;br&gt;Join one of our most exciting projects as a Software Engineer, in order to build the next successful startup across the globe.&lt;br&gt;&lt;br&gt;&lt;br&gt;&lt;strong&gt;What they offer:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Highly professional and dynamic IT team&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Profit from the broad expertise and multiculturality of your team members&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Core development and optimization of your company''s website&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Autonomous development and implementation of new features to bring the website to the next level&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Agile, cross-functional team with lots of responsibility on the team level&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Build platforms and web applications for our startup companies that reach millions of users in 100+ countries every day&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/91809/senior-software-engineer-m-f-rocket-internet-rocket-internet-company', 'Wed, 07 Oct 2015 14:02:02 Z', '2015-10-07T14:02:02Z', 991);
