INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (993, 'Remote iOS &amp; Native App Developer (Swift, Go, JavaScript) at Springboard Retail (Boston, MA) (allows remote)', '&lt;p&gt;The team at Springboard Retail is looking for an iOS/native application developer.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a retail and point-of-sale application, we have to provide a seamless interface between our web application and the underlying operating system to interact with a variety of hardware peripherals. You would help with our efforts to provide a consistently excellent experience across all of our supported platforms, both mobile (iOS, possibly Android in future) and desktop (Mac OS X, Windows).&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We&amp;rsquo;re looking for a versatile, polyglot developer excited by the prospect of working with a broad range of technologies.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This is a long term, full-time contract position. It doesn&amp;rsquo;t matter where you&amp;rsquo;re located. Remote work is no problem.&lt;/p&gt;', 'Boston, MA', 'http://careers.stackoverflow.com/jobs/72678/remote-ios-native-app-developer-swift-go-springboard-retail', 'Wed, 07 Oct 2015 14:02:02 Z', '2015-10-07T14:02:02Z', 993);
