INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (995, 'Softwareentwickler (m/w) fr Webanwendungen at up2date solutions GmbH (Nrnberg, Deutschland)', '&lt;p&gt;&lt;strong&gt;Deine Aufgaben:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Weiterentwicklung unserer Veranstaltungssoftware (Design und Implementierung neuer&amp;nbsp;Features, Qualit&amp;auml;tssicherung)&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Entwicklung von Schnittstellen zu externen Systemen&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Wir bieten:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Interessante Aufgaben in einem innovativen Unternehmen&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Angenehme Arbeitsatmosph&amp;auml;re in einem jungen Team&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Flexible Arbeitszeiten&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Nrnberg, Deutschland', 'http://careers.stackoverflow.com/jobs/71679/softwareentwickler-m-w-f%C3%BCr-webanwendungen-up2date-solutions-gmbh', 'Wed, 07 Oct 2015 13:02:02 Z', '2015-10-07T13:02:02Z', 995);
