INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (997, 'Mobile Application Developer Android/iOS (m/f) - New Rocket Company at New Rocket Company (Berlin, Germany)', '&lt;p&gt;Rocket''s mission is to become the world''s largest Internet platform outside of the United States and China. Some of its latest projects focus on building consumer facing mobile products and services, available to you everywhere and at anytime on your phone screen. Rocket is gearing up towards disrupting existing online business models with mobile first strategy. To achieve this, they are looking for you to be part of their next big mobile team from day one.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Join one of our most exciting projects as an iOS or Android Mobile Developer in order to build one of the next successful Rocket-startups around the world.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What you will do:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Implement, optimize and maintain this new company&amp;rsquo;s app&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Most responsible for the work and solutions you provide for the users&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Collaborate with product managers, UI and UX designers, data scientists and engineers to build simple, scalable and innovative mobile experiences&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Participate in code reviews and mobile application debugging and diagnosis&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Manage the complete mobile development cycle including testing, integration and publishing&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Take responsibility for assigning resources to design and implement workflows which meet quality and time targets&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/90265/mobile-application-developer-android-ios-m-f-new-rocket-company', 'Wed, 07 Oct 2015 13:02:02 Z', '2015-10-07T13:02:02Z', 997);
