INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (999, 'CDI  40 Ingnieurs Dveloppement Logiciel R&amp;D H/F at Dassault Systmes (Vlizy-Villacoublay, France)', '&lt;p&gt;Afin &amp;nbsp;d&amp;rsquo;accompagner la croissance du Groupe, DASSAULT SYSTEMES recrute en France 40 ing&amp;eacute;nieurs pour rejoindre ses &amp;eacute;quipes de d&amp;eacute;veloppement R&amp;amp;D.&lt;br&gt;Bas&amp;eacute; au si&amp;egrave;ge mondial et au sein du plus important centre de R&amp;amp;D, vous prendrez part &amp;agrave; des projets innovants dans un contexte international et pourrez d&amp;eacute;velopper vos comp&amp;eacute;tences sur les technologies suivantes :&lt;br&gt;-&amp;nbsp;&amp;nbsp; &amp;nbsp;Cloud Computing / Virtualisation&lt;br&gt;-&amp;nbsp;&amp;nbsp; &amp;nbsp;Big Data&amp;nbsp;&lt;br&gt;-&amp;nbsp;&amp;nbsp; &amp;nbsp;Applications sur plateformes mobiles&lt;br&gt;-&amp;nbsp;&amp;nbsp; &amp;nbsp;Web (front &amp;amp; back end) : JS/CSS/HTML&lt;br&gt;-&amp;nbsp;&amp;nbsp; &amp;nbsp;D&amp;eacute;veloppement logiciel : JAVA / C++&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Nos projets vous permettront de participer &amp;agrave; des activit&amp;eacute;s telles que :&lt;br&gt;-&amp;nbsp;&amp;nbsp; &amp;nbsp;La mise en &amp;oelig;uvre des processus et des outils de d&amp;eacute;veloppement&lt;br&gt;-&amp;nbsp;&amp;nbsp; &amp;nbsp;La gestion du cycle de vie du d&amp;eacute;veloppement logiciel&lt;br&gt;-&amp;nbsp;&amp;nbsp; &amp;nbsp;L&amp;rsquo;architecture logicielle adapt&amp;eacute;e &amp;agrave; nos besoins et nos contraintes&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Vous travaillerez dans un environnement dynamique, port&amp;eacute; par l&amp;rsquo;innovation qui vous permettra &amp;nbsp;de d&amp;eacute;velopper vos comp&amp;eacute;tences techniques en prenant part &amp;agrave; des projets autant vari&amp;eacute;s qu&amp;rsquo;hautement technologiques.&lt;/p&gt;', 'Vlizy-Villacoublay, France', 'http://careers.stackoverflow.com/jobs/99759/cdi-40-ing%C