INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1001, 'QA Senior Test Analyst at Rocket Internet SE (Berlin, Deutschland)', '&lt;p&gt;As Rocket''s&lt;strong&gt; QA Senior Test Analyst&lt;/strong&gt;, you will help us launch the most successful startup companies around the world.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Encounter extensive testing experiences of web and mobile applications, from functional testing to regression testing.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Find bugs.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Set up test strategies and test cases, in a running SDLC, and adapting the flow in a high-speed and highly professional team Requirements.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Monitoring of automated tests with Cucumber&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Deutschland', 'http://careers.stackoverflow.com/jobs/99756/qa-senior-test-analyst-rocket-internet-se', 'Wed, 07 Oct 2015 13:02:02 Z', '2015-10-07T13:02:02Z', 626);
