INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1005, 'QA Senior Test Automation Engineer at Rocket Internet SE (Berlin, Deutschland)', '&lt;p&gt;As a &lt;strong&gt;QA Senior Test Automation Engineer&lt;/strong&gt; at Rocket you will help us launch the most successful startup companies around the world.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Automated testing of web and mobile applications&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Develop automated scenarios/scripts using Cucumber (for web applications) and Calabash (for mobile applications)&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Optimize existing test cases to get more stability and efficiency&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Run automated functional tests as well as performance and load tests&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Analyze automated test results and report bugs to responsible employees&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Support the test automation team during the whole development process (starting from the analysis of requirements up to the integration of automated test cases into the CI system (Jenkins)&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Deutschland', 'http://careers.stackoverflow.com/jobs/99755/qa-senior-test-automation-engineer-rocket-internet-se', 'Wed, 07 Oct 2015 13:02:02 Z', '2015-10-07T13:02:02Z', 626);
