INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1006, 'Senior Developer / Software-Entwickler Java / JEE / agil (w/m) in Mnchen at iteratec GmbH (Unterhaching, Deutschland)', '&lt;p&gt;&lt;strong&gt;Typisch iteratec&lt;/strong&gt;&amp;nbsp;&lt;br&gt; Wir entlasten unsere Kunden durch ma&amp;szlig;geschneiderte Softwarel&amp;ouml;sungen und hochwertige Beratung.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Wir entwickeln individuelle Softwaresysteme (wie z. B.&amp;nbsp;&lt;a href="http://www.iteratec.de/de/content/realisierung-eines-carsharing-services-automotive" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Carsharing f&amp;uuml;r BMW / DriveNow&lt;/a&gt;) und sind an hochspannenden Technologieprojekten wie der&amp;nbsp;&lt;a href="http://www.iteratec.de/de/content/mitwirkung-bei-der-realisierung-von-mini-augmented-vision-datenbrille" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;MINI Augmented Vision Datenbrille&lt;/a&gt;&amp;nbsp;oder dem neuen Otto-Shop beteiligt. Dar&amp;uuml;ber hinaus beraten wir unsere renommierten Kunden in technologischen und methodischen Fragestellungen sowie im Bereich IT-Management.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Warum iteratec&amp;nbsp;&lt;/strong&gt;&lt;br&gt; iteratec ist ein inhabergef&amp;uuml;hrter IT-Dienstleister. Kurze Entscheidungswege erm&amp;ouml;glichen es Ihnen, ab dem ersten Tag Ideen und Know-how einzubringen. Aufgrund der Vielfalt unserer Projekte werden Sie Erfahrung in unterschiedlichen Branchen auf- bzw. ausbauen. Wir arbeiten ergebnisorientiert und bieten Ihnen flexible Arbeitszeiten. Die j&amp;auml;hrlichen Gehalts&amp;uuml;berpr&amp;uuml;fungen sorgen f&amp;uuml;r eine faire und angemessene Bezahlung.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Die Grundlage f&amp;uuml;r unseren Erfolg und das stetige Wachstum ist unser exzellentes Team und die mitarbeiterorientierte Unternehmenskultur. Um unser Weiterbildungsangebot, die Gesundheitsf&amp;uuml;rsorge und die Sozialleistungen zielgerichtet weiterentwickeln zu k&amp;ouml;nnen, f&amp;uuml;hren wir regelm&amp;auml;&amp;szlig;ig Mitarbeiterbefragungen durch.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Was Sie erwartet&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Mitarbeit in spannenden Projekten und die M&amp;ouml;glichkeit, schnell Verantwortung zu &amp;uuml;bernehmen&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Softwareentwicklung in agilen Umgebungen mit modernen Vorgehensmodellen &amp;uuml;ber den gesamten Entwicklungsprozess&lt;/li&gt;&lt;br /&gt;&lt;li&gt;St&amp;auml;ndig neue Herausforderungen durch aktuelle Themen und Technologien&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Entwicklungsm&amp;ouml;glichkeiten zum Software-Architekten, Projektleiter oder IT-Berater&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Teilnahme am&amp;nbsp;&lt;a href="http://www.iteratec.de/de/content/innovation" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;Innovation Frei-Day&lt;/a&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Die Projektsprache ist in der Regel Deutsch&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;&lt;strong&gt;&lt;br&gt; Bewerben Sie sich&lt;br&gt; &lt;/strong&gt;Personalfragen sind Chefsache. Senden Sie Ihre kompletten Bewerbungsunterlagen direkt an unseren Gesch&amp;auml;ftsf&amp;uuml;hrer Klaus Eberhardt (Klaus.Eberhardt@iteratec.de). Wir freuen uns darauf, Sie kennenzulernen.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Bei Fragen steht Ihnen Frau Christina Hirschauer unter der 089-614551-311 gerne zur Verf&amp;uuml;gung.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Wir sind&amp;nbsp;ausgezeichnet&lt;/strong&gt;&amp;nbsp;&lt;strong&gt;&amp;ndash;&lt;/strong&gt;&amp;nbsp;&lt;strong&gt;z. B. als einer der &amp;bdquo;Besten Arbeitgeber Deutschlands &amp;ldquo; und auf&amp;nbsp;&lt;/strong&gt;&lt;a href="http://www.kununu.com/de/all/de/it/iteratec" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;&lt;strong&gt;kununu&lt;/strong&gt;&lt;/a&gt;&lt;strong&gt;&amp;nbsp;als eines der zehn besten deutschen Unternehmen in der Branche EDV &amp;amp; IT&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;iteratec GmbH&lt;br&gt; Inselkammerstra&amp;szlig;e 4&lt;br&gt; 82008 M&amp;uuml;nchen-Unterhaching&lt;br&gt; Tel.: +49 89 614551 0&lt;br&gt; &lt;br&gt; &lt;a href="http://www.iteratec.de/" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;www.iteratec.de&lt;/a&gt;&amp;nbsp;-&amp;nbsp;&lt;a href="https://twitter.com/iteratec" rel="nofollow" rel="nofollow" rel="nofollow" rel="nofollow"&gt;twitter.com/iteratec&lt;/a&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Gesch&amp;auml;ftsstellen in Frankfurt, Hamburg, M&amp;uuml;nchen, Stuttgart, Wien und Z&amp;uuml;rich&lt;/p&gt;', 'Unterhaching, Deutschland', 'http://careers.stackoverflow.com/jobs/99754/senior-developer-software-entwickler-java-jee-iteratec-gmbh', 'Wed, 07 Oct 2015 13:02:02 Z', '2015-10-07T13:02:02Z', 1006);
