INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1013, 'Junior Technical ABAP Consultant (m/w) at abilis GmbH IT-Services &amp; Consulting (Stutensee, Deutschland)', '&lt;p&gt;Starten Sie durch mit abilis iC:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Gestalten Sie das Wachstum in &lt;br&gt;einem aufstrebenden Unternehmen!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;F&amp;uuml;r unseren Firmensitz in Stutensee suchen wir zum n&amp;auml;chstm&amp;ouml;glichen Zeitpunkt:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Junior Technical ABAP Consultant (m/w)&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Wer wir sind&amp;hellip;&lt;br&gt;Die abilis GmbH bedient als SAP Gold Partner den klassischen Mittelstand &amp;uuml;ber die gesamte Wertsch&amp;ouml;p-fungskette: Von der Beratung &amp;uuml;ber die Einf&amp;uuml;hrung bis hin zum Betrieb der L&amp;ouml;sungen in den eigenen Green IT- Hochleistungsrechenzentren. Der Fokus liegt u.a. auf der mittelst&amp;auml;ndischen Fertigungsindustrie, in der wir auf einen beeindruckenden Erfahrungsschatz und auf eine jahrzehntelange Zusammenarbeit mit der SAP zur&amp;uuml;ckblicken k&amp;ouml;nnen.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;Ihre Aufgaben bei abilis:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Als Junior Technical ABAP Consultant (m/w)&amp;hellip;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- erstellen Sie Konzepte und realisieren Erweite-rungen in den g&amp;auml;ngigen SAP Modulen&lt;br&gt;- setzen Sie individuell erstellte ABAP Transaktio-nen und Reports um&lt;br&gt;- entwickeln Sie Schnittstellen im SAP-Umfeld (RFC/BAPI, Webservices,&amp;hellip;)&lt;br&gt;- entwickeln Sie (mobile) L&amp;ouml;sungen &amp;amp; Add-ons&lt;br&gt;- unterst&amp;uuml;tzen Sie Migrations- und Integrations-projekte&lt;br&gt;- entwickeln Sie Formulare: Smartforms, SAPScript, Adobe Forms (Druck und Interaktiv)&lt;br&gt;- geh&amp;ouml;rt auch der Support vorhandener Entwick-lungsl&amp;ouml;sungen zu Ihrem Aufgabengebiet&lt;br&gt;uvm.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;Was Sie mitbringen:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Ein Studium mit IT- Hintergrund&lt;br&gt;- Erste praktische Erfahrungen in der Entwicklung mit SAP ABAP sind von Vorteil, aber kein Muss &lt;br&gt;- Prozess-Kenntnisse im Logistik-, Produktions- oder Rechnungswesen / Controlling-Umfeld sind von Vorteil, aber kein Muss&lt;br&gt;- Spa&amp;szlig;, SAP-L&amp;ouml;sungen zu entwickeln und zu verbessern&lt;br&gt;- Analytisches, pragmatisches und strukturiertes Denkverm&amp;ouml;gen und Vorgehen&lt;br&gt;- Kunden- und vertriebsorientierte Arbeitsweise&lt;br&gt;- Regionale Reisebereitschaft (Tagesreisen)&lt;br&gt;- Kommunikationsst&amp;auml;rke und Teamf&amp;auml;higkeit&lt;br&gt;- Innovationsgeist und Kreativit&amp;auml;t&lt;br&gt;- Gute Englischkenntnisse&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;hellip; und was wir bieten:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Attraktives, dynamisches Gehaltsmodell, mit einer von abilis finanzierten Altersvorsorge&lt;br&gt;- Flache Hierarchien und kurze Entscheidungswege&lt;br&gt;- Professionelles, sympathisches und famili&amp;auml;res Team&lt;br&gt;- Platz f&amp;uuml;r Kreativit&amp;auml;t und Entfaltung&lt;br&gt;- Individuelle Entwicklungs- &amp;amp; Weiterbildungsm&amp;ouml;glich-keiten&lt;br&gt;- Unbefristete Festanstellung&lt;/p&gt;&lt;br /&gt;&lt;p&gt;- Einsatzort: Prim&amp;auml;r Stutensee (Karlsruhe)&lt;br&gt;- Sehr geringe Reiset&amp;auml;tigkeit &amp;amp; humane Arbeitszei-ten f&amp;uuml;r die Consulting-Branche&lt;br&gt;- Wohlergehen: Massagesessel, w&amp;ouml;chentliche Massageangebote, Tischkicker, freie Getr&amp;auml;nke, Terrasse mit Sitzgele-genheit, ausreichend Parkpl&amp;auml;tze, Firmenevents, Gesundheitsf&amp;ouml;rderung, Natur, &amp;hellip;&amp;hellip;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Interesse geweckt? Bewerben Sie sich noch heute unter karriere@abilis.de&lt;br&gt;Wir freuen uns, von Ihnen zu h&amp;ouml;ren!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;abilis GmbH IT-Services &amp;amp; Consulting, Lorenzstr. 8, 76297 Stutensee&lt;br&gt;Telefon: +49 (0) 7244 7080 0, Fax:: +49 (0) 7244 7080 105&lt;br&gt;E-Mail: info@abilis.de , Internet: www.abilis.de&lt;/p&gt;', 'Stutensee, Deutschland', 'http://careers.stackoverflow.com/jobs/99752/junior-technical-abap-consultant-m-w-abilis-gmbh-it-services', 'Wed, 07 Oct 2015 12:02:02 Z', '2015-10-07T12:02:02Z', 1013);
