INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1015, 'Software Engineer at Planet Payment, Inc. (Melbourne, FL)', '&lt;p&gt;We have an excellent opportunity and an immediate need for a &lt;strong&gt;Software Engineer&lt;/strong&gt; in our&lt;strong&gt; Melbourne, FL &lt;/strong&gt;office.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are looking for a talented and motivated Software Engineer to help build and evolve our Java web applications in an agile, test driven development environment. You will work with a small cross-functional team to design, build, and maintain web applications on the Java platform.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Work Environment:&amp;nbsp; &lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Fast-paced and holds a high level of responsibility to ensure timelines are met accordingly.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&amp;nbsp;This opportunity offers excellent compensation including a comprehensive benefits package.&amp;nbsp; If you possess the skills that will allow you to excel in this role, then, please forward your resume WITH your salary requirements for immediate attention.&amp;nbsp; EOE.&amp;nbsp;&lt;/p&gt;', 'Melbourne, FL', 'http://careers.stackoverflow.com/jobs/99751/software-engineer-planet-payment-inc', 'Wed, 07 Oct 2015 12:02:02 Z', '2015-10-07T12:02:02Z', 1015);
