INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1019, 'Mobile Application Developer Android/iOS at Rocket Internet SE (Berlin, Germany)', '&lt;p&gt;As a Mobile Engineer at Rocket Internet you help launching and supporting successful startup companies all over the world.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;If global scale and speed of execution is exciting to you, if you are passionate about your craft and if you are striving to build apps that matter to millions of people around the world, Rocket Internet is the right place for you!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Growing at an incredible pace we&amp;rsquo;re looking for sharp and passionate mobile engineers that help us make our mobile applications the best in the world.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;What we offer:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Agile, cross-functional team with lots of responsibility on the team level&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Build 200+ mobile applications for our startup companies that reach millions of users in 100+ countries every day&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Many opportunities to grow on a personal and professional level&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Work with great people from many countries&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Challenging projects&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Competitive salary&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;br /&gt;&lt;p&gt;Relocation package ensuring a smooth transition to work and live in Berlin&lt;/p&gt;&lt;br /&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/97211/mobile-application-developer-android-ios-rocket-internet-se', 'Wed, 07 Oct 2015 12:02:02 Z', '2015-10-07T12:02:02Z', 626);
