INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1022, 'Frontend Developer at Impero (London, UK)', '&lt;p&gt;We&amp;acute;re looking for people that love to code and to work on all kinds of digital projects. You&amp;rsquo;ll be at the center of the creative process so a good appreciation of design and usability will be required.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Code is the heart and soul of what we do so you&amp;rsquo;ll work alongside Creatives and Designers to push the boundaries of what browsers can do to make any project look and feel amazing.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You&amp;rsquo;ll work on interesting projects everyday. From small interactive websites to large complex systems. We want you to be part of our ongoing obsession of creating beautiful code and contributing to our internal tools. &lt;/p&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/99654/frontend-developer-impero', 'Wed, 07 Oct 2015 11:02:02 Z', '2015-10-07T11:02:02Z', 1022);
