INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1025, 'Mobile Web Developer at KAYAK (Cambridge, MA)', '&lt;p&gt;&lt;strong&gt;Position&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;KAYAK is looking for smart, enthusiastic web developers to take our industry-leading mobile and tablet sites to the next level. Our team is constantly iterating, designing, and coding our way to excellence. We''re building the compelling user experiences that keep our mobile products a step ahead of the competition. If you''re an experienced UI engineer who wants to work on a team of similarly talented, driven individuals, then we''d love to hear from you.&lt;/p&gt;', 'Cambridge, MA', 'http://careers.stackoverflow.com/jobs/94978/mobile-web-developer-kayak', 'Wed, 07 Oct 2015 11:02:02 Z', '2015-10-07T11:02:02Z', 1025);
