INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1031, 'Software Developer (D language) at sociomantic labs GmbH (Berlin, Germany)', '&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Your Mission&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Support our team in the development of our event-based infrastructure&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Development of high-performance applications and services&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Writing applications to work with our distributed DHT database system&lt;/li&gt;&lt;br /&gt;&lt;li&gt;You will be coding in the D-language&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;&lt;strong&gt;Our Promise&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Employment in Berlin, full-time and full of fun challenges, with flexible working hours&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Access to a high-profile professional network of international Internet companies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Possibility to show your excellent competence and your creative ideas to a broad audience&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A competitive compensation and incentive plan that rocks when you rock&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Personal development and training that will help you evolve from the pro you are right now to the champ you&amp;rsquo;re destined to be&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Basic German language courses for non-native speakers&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Help with residence permit processing for non-EU citizens&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Daily adrenalin rushes while working and learning in one of the fastest growing sectors in online advertising&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Access to an international high-profile network&lt;/li&gt;&lt;br /&gt;&lt;li&gt;A company culture driven by pioneer-thinking and talent that exceeds departments and hierarchies&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Berlin, Germany', 'http://careers.stackoverflow.com/jobs/99740/software-developer-d-language-sociomantic-labs-gmbh', 'Wed, 07 Oct 2015 10:02:02 Z', '2015-10-07T10:02:02Z', 1031);
