INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1045, 'Backend PHP Developer, Chesterfield, Derbyshire, Excellent at Creativity Sells Ltd (Chesterfield, UK)', '&lt;p&gt;We''re leading the way in website development for motorcycle dealerships across the UK and virtually all of the UK''s top dealers are now using our solutions. We&amp;rsquo;re looking for another talented Web Developer to join our team.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will help to manage and take the lead on project development, implementation, testing and deployment and will have a strong opportunity to grow with the market leader in what is a very well established and huge market.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will become a vital part of this close-knit team, not just another name on the payroll. Long-term career prospects are very strong - the ethos of the directors is to run a very happy ship and employees are viewed as vital to the future success.&lt;/p&gt;', 'Chesterfield, UK', 'http://careers.stackoverflow.com/jobs/99736/backend-php-developer-chesterfield-derbyshire-creativity-sells-ltd', 'Wed, 07 Oct 2015 10:02:02 Z', '2015-10-07T10:02:02Z', 1045);
