INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1052, 'Data Solution Architect at Microsoft (Reading, UK)', '&lt;p&gt;A Data Solution Architect (DSA) drives high priority customer initiatives, leveraging Azure data services to solve the biggest and most complex data challenges faced by Microsoft&amp;rsquo;s enterprise customers. The DSA is a technical, customer facing role, accountable for the end-to-end customer deployment and usage experience for Azure data services. &amp;nbsp; DSAs own the Azure technical customer engagement including: architectural design sessions, implementation projects and/or Proofs of Concepts. The ideal candidate will have experience in customer facing roles and success leading deep technical architecture and design discussions with senior executives.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Key Responsibilities&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Interface with Microsoft and partner sales/delivery teams to drive consumption of Azure data and analytics services, including SQL Database, SQL Server on Azure VMs, Document DB, SQL Data Warehouse, HDInsight, Machine Learning, Stream Analytics, Data Factory, Event Hubs and Notification Hubs&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Architect scalable data processing and analytics solutions, including technical feasibility and proposal development for Big Data storage, processing and consumption (e.g., development of enterprise Data Lake strategy, heterogeneous data management, Polyglot Persistence, decision support/BI over Data Lake)&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Design, coordinate and execute pilots, prototypes or proof of concepts, provide validation on specific scenarios&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Drive the quality of the onboarding plan (with Microsoft Consulting Services or partners)&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Report on progress of customer as business objectives; Ensure plan execution by Partners&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Document and share technical best practices / insights with Engineering teams and the solution architect community&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;Experiences&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Demonstrable experience with deep understanding in databases and analytics, including relational databases (e.g., SQL Server, MySQL, Oracle), data warehousing, big data (Hadoop, Spark), business intelligence (Reporting Services, Power BI), NoSQL, and analytics&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Demonstrable success in consultative/complex technical sales and deployment projects (where necessary, managing various stakeholder relationships to get consensus on solution/projects)&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Experience in SQL DB, DB Lift &amp;amp; Shift to public cloud, designing hybrid cloud solutions and cloud migrations, IOT&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Understanding of big data use-cases and Hadoop-based design patterns&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Highly valued: Knowledge of real time/stream analytics&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Highly valued: experience using R; experience with Marketing or Risk analytics solutions for Enterprises&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Deep understanding of cloud computing technologies, business drivers, and emerging computing trends&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Proven track record of building deep technical relationships with senior executives and growing cloud consumption share in large or highly strategic accounts&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Problem-solving mentality leveraging internal and/or external resources&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Exceptional verbal and written communication&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Oversight experience on major transformation projects and successful transitions to implementation support teams&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&amp;bull; &amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; Presentation skills with a high degree of comfort with both large and small audiences&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Prior work experience in a DBA/Consulting/Architecture position within a software and/or services company such as Amazon, Palantir, Google, IBM, Oracle, SAP, Teradata, Tableau, Qlik, MicroStrategy, Wipro, CSC, HP, Infosys, Cognizant&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;A technical Bachelors degree in Computer Science or mathematics would be desirable.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Microsoft is an equal opportunity employer and supports workforce diversity, we offer competitive base salary, bonus and an excellent benefits package.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Microsoft&amp;rsquo;s privacy statement can be viewed on the following web page:&lt;/p&gt;&lt;br /&gt;&lt;p&gt;http://privacy.microsoft.com/en-gb/default.mspx&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;AZ16&lt;/p&gt;', 'Reading, UK', 'http://careers.stackoverflow.com/jobs/99731/data-solution-architect-microsoft', 'Wed, 07 Oct 2015 09:02:02 Z', '2015-10-07T09:02:02Z', 506);
