INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1058, 'Senior C# Entwickler Vollzeit at oli.k computer und internetservice e.K. (Dresden, Deutschland)', '&lt;p&gt;Wenn sie Interesse&amp;nbsp;haben, uns in der Optimierung und Implementierung von Gesch&amp;auml;ftsprozessen in bestehenden und neuen Applikationen zu unterst&amp;uuml;tzen und in der Pflege und Entwicklung von datenbankbasierenden Anwendungen t&amp;auml;tig zu sein, dann kommen sie zu uns. Aufgrund der flachen&amp;nbsp;Hierarchien und unkomplizierter Arbeitsweise&amp;nbsp;treffen sie die direkte Abstimmung mit unseren Fachabteilungen.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Sollten sie Interesse haben sich langfristig weiter zu entwickeln, haben sie die M&amp;ouml;glichkeit als Schnittstelle/ Koordination zwischen Gesch&amp;auml;ftsf&amp;uuml;hrung und Team zu agieren und die Leitung des Teams von 2-3 Mitarbeitern zu &amp;uuml;bernehmen. Wir bieten eine Vollzeitstelle (40 Wo./Std.), Einsatz von Montag-Freitag. Bei uns k&amp;ouml;nnen sie sich die Arbeitszeiten frei einteilen (Gleitzeit -Kernarbeitszeit 10h-16h).Teilweise Home-Office m&amp;ouml;glich.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Die Stelle ist ab sofort zu besetzen.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Wir freuen uns auf Ihre Bewerbung.&lt;/p&gt;', 'Dresden, Deutschland', 'http://careers.stackoverflow.com/jobs/96861/senior-c-sharp-entwickler-vollzeit-olik-computer-und', 'Wed, 07 Oct 2015 09:02:02 Z', '2015-10-07T09:02:02Z', 1058);
