INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1061, 'Cloud Architect m/f at Cloudreach GmbH (Mnchen, Germany)', '&lt;p&gt;&lt;strong&gt;&lt;em&gt;How will you spend your time at Cloudreach?&lt;/em&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will be working in the following areas (and more!):&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Consulting with our German customers helping build and architect new solutions, data center migrations and scaling their adoption of cloud technologies&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Green field development of Amazon Web Services infrastructure (EC2, RDS, EMR, etc), e.g. looking at an existing service or data centre and then architecting a more effective solution in AWS. You might work on a major eCommerce platform, a MySQL database migration, a Big Data project, or building a scalable mobile backend&lt;/li&gt;&lt;br /&gt;&lt;li&gt;DevOps: Systems engineering work with frameworks such as Chef or Puppet - as well as helping to break down barriers between silo&amp;rsquo;d teams. We automate everything&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Not doing any out of hours shift work - yay!&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Mnchen, Germany', 'http://careers.stackoverflow.com/jobs/97198/cloud-architect-m-f-cloudreach-gmbh', 'Wed, 07 Oct 2015 09:02:02 Z', '2015-10-07T09:02:02Z', 1061);
