INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1063, 'Senior Node.js-Entwickler (m/w) at tresmo GmbH (Augsburg, Germany)', '&lt;p&gt;tresmo macht Unternehmen digital erfolgreich. Als 360&amp;deg;-Digitalagentur unterst&amp;uuml;tzen wir unsere Kunden ganzheitlich&amp;nbsp;&amp;ndash; von der Beratung zur digitalen Transformation &amp;uuml;ber Design, Technologieauswahl und Programmierung&amp;nbsp;bis hin zu Big Data-L&amp;ouml;sungen. Unser Kundenspektrum reicht vom Start-up bis zum internationalen Gro&amp;szlig;konzern.&lt;br&gt;In &amp;uuml;ber 1000 erfolgreichen Online-, Mobile- und IoT-Projekten konnte unser Kernteam u.a. schon Groupon, VELUX,&amp;nbsp;Viessmann, BMW, Venture Stars und Lindner Hotels begeistern. Unser Erfolg beruht v.a. auf der Kompetenz und&amp;nbsp;Leidenschaft unseres interdisziplin&amp;auml;ren Teams aus erfahrenen Business- und Technologieexperten.&lt;br&gt;Dieses m&amp;ouml;chten wir gern weiter ausbauen und suchen ab sofort f&amp;uuml;r unseren Standort in Augsburg einen&amp;nbsp;Senior Node.js-Entwickler (m/w).&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;Ihre Aufgaben:&lt;br&gt;-&amp;nbsp;Entwicklung von RESTful APIs f&amp;uuml;r unsere Softwareprojekte&lt;br&gt;-&amp;nbsp;Design von Software-Architekturen&lt;br&gt;-&amp;nbsp;Weiterentwicklung unseres Technologie-Stacks (inkl. Pr&amp;uuml;fung und Empfehlung neuer Technologien)&lt;/p&gt;', 'Augsburg, Germany', 'http://careers.stackoverflow.com/jobs/99727/senior-nodejs-entwickler-m-w-tresmo-gmbh', 'Wed, 07 Oct 2015 09:02:02 Z', '2015-10-07T09:02:02Z', 1063);
