INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1078, 'Technical Architects at Department for Work &amp; Pensions (London, UK)', '&lt;p&gt;&lt;strong&gt;Location: Leeds, Newcastle, Sheffield, Blackpool, Warrington&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;An additional allowance is also available for exceptional candidates&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Do you have the desire and ambition to come and shake up outdated Government technology and fundamentally transform the way our business operates?&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We invite you to join one of the largest technology challenges in the UK today - a once &amp;nbsp;in a generation programme of transformational change&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Here at the Department for Work and Pensions (DWP) we are re-platforming our entire IT onto new hardware, operating systems, application server technologies and application integrations, conducting large scale data migration, introducing new functionality and modernising front end user interfaces and applications. These IT systems underpin the services that support 22m citizens, annual processing of 7.35m benefit claims, payment of &amp;pound;165bn in benefits and pensions, and over 90,000 employees across 950 locations.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We are keen to recruit the best architecture talent out there, people who have hands on delivered multi-tier architectures/applications and micro-service architectures, with a passion and detailed technical knowledge of any of the following:-&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Web technologies including HTTP, HTML, CSS, XML, JSON, SOAP, REST&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Enterprise scale database technologies including Oracle &amp;amp; Enterprise DB and NoSQL, Cassandra &amp;amp; MongoDB&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Operating systems including Linux &amp;amp; Solaris.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Application server platforms including as Websphere &amp;amp; Jboss&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Virtualisation technologies including as VMware, OVM, HyperV&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'London, UK', 'http://careers.stackoverflow.com/jobs/97188/technical-architects-department-for-work-pensions', 'Wed, 07 Oct 2015 08:02:02 Z', '2015-10-07T08:02:02Z', 1072);
