INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1085, 'Data Engineer - Dubai at Propertyfinder (Dubai, United Arab Emirates)', '&lt;p&gt;We are looking for a highly experienced Data Engineer/Data Scientist to join our team at Propertyfinder. Reporting to the Analytic Manager, the right candidate will be helping with gathering and processing raw data at scale (including writing scripts, web scraping, calling APIs, write SQL queries, etc.).&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Tasks and responsibilities&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Processing unstructured data into a form suitable for analysis &amp;ndash; and then do the analysis&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Supporting business decisions with ad hoc analysis as needed&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Experience with EMR and other web services will help you hit the ground running since we use AWS extensively&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Taking full ownership of exciting data science projects mainly related to recommendation engines and learning to rank fields&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Helping with the analysis of Propertyfinder products.&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Dubai, United Arab Emirates', 'http://careers.stackoverflow.com/jobs/99720/data-engineer-dubai-propertyfinder', 'Wed, 07 Oct 2015 07:02:02 Z', '2015-10-07T07:02:02Z', 1085);
