INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1090, 'Startup Software Engineer (Python and more) at Storj Labs Inc. (Atlanta, GA)', '&lt;p&gt;Are you an amazing Python developer who is passionate about decentralization and the promise of blockchain-based technologies? Do you want to join a team that is revolutionizing the world of cloud storage? If so, the Storj team would love to meet you!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;We&amp;rsquo;re looking for full-time Python developers to support and expand the development of our core technology and flagship applications: MetaDisk and DriveShare.&amp;nbsp;This is a full time position at our Atlanta office. Relocation assistance is generally not offered, and remote applications are generally not considered.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;You will&amp;nbsp;be part of&amp;nbsp;the core development team of an early-stage&amp;nbsp;startup looking to release a product this year. You should be passionate, dedicated, and live to create, like we do.&lt;/p&gt;', 'Atlanta, GA', 'http://careers.stackoverflow.com/jobs/99718/startup-software-engineer-python-and-more-storj-labs-inc', 'Wed, 07 Oct 2015 06:02:02 Z', '2015-10-07T06:02:02Z', 1090);
