INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1096, 'Creative Technologist at Portable (Collingwood, Australia)', '&lt;p&gt;&lt;span&gt;We&amp;rsquo;re looking for a Creative Technologist. We need somebody with all round technical ability and passion, and the desire to design solutions and assemble and inspire teams to deliver world class code. An awareness of business issues, especially in a small or startup type environment would be beneficial and highly regarded.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;We have a flat structure, so you&amp;rsquo;ll be working side-by-side with the whole team, from initial workshops and concepting through to delivery.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;This will be a hands-on role in a small team, so there will be plenty of doing. At Portable you&amp;rsquo;ll find the resources and senior talent to complement your aspirations.&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;span&gt;If you&amp;rsquo;d like to apply, please submit your resume, and tell us a story about yoursel&lt;/span&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;&lt;span&gt;You&amp;rsquo;ll be rewarded with strategic, and executive management support, and a relaxed and flexible working environment.&lt;/span&gt;&lt;/p&gt;', 'Collingwood, Australia', 'http://careers.stackoverflow.com/jobs/99716/creative-technologist-portable', 'Wed, 07 Oct 2015 04:02:02 Z', '2015-10-07T04:02:02Z', 1096);
