INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1102, 'Sr. Software Engineer, Web at Vevo (San Francisco, CA)', '&lt;p&gt;&lt;strong&gt;&lt;strong&gt;About the Role:&lt;/strong&gt;&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a Sr. Software Engineer engineer at Vevo, you&amp;rsquo;ll have the opportunity to develop features using the latest cutting edge technologies&amp;nbsp;that will reach millions of music video fans. You&amp;rsquo;ll be joining a small team that owns the entire feature set for our web and mobile web. Apply to this job if you want to work on a small team, iterate quickly, and collaborate with other talented engineers, designers, and product managers.&lt;/p&gt;', 'San Francisco, CA', 'http://careers.stackoverflow.com/jobs/99712/sr-software-engineer-web-vevo', 'Wed, 07 Oct 2015 01:02:02 Z', '2015-10-07T01:02:02Z', 1102);
