INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1112, 'QA Engineer at eFolder (Littleton, CO)', '&lt;p&gt;The Quality Assurance Engineer supports an Agile software development lifecycle by working as a vital cross functional team member. The preferred candidate will have experience creating and maintaining automated regression and smoke tests for web and software applications. They will develop, publish, and maintain automated tests for complex, multi-tier, distributed applications throughout the full lifecycle of the software.&lt;br&gt; &lt;br&gt; The QA Engineer is involved with creating, updating and executing automated tests as part of eFolder''s Backup and BDR development team and is tasked with verifying the results of those tests. &amp;nbsp;They will be responsible for managing and deploying development builds to internal environments, testing new&amp;nbsp;features,&amp;nbsp;verifying existing functionality and formalizing our QA process.&lt;/p&gt;', 'Littleton, CO', 'http://careers.stackoverflow.com/jobs/99709/qa-engineer-efolder', 'Wed, 07 Oct 2015 00:02:02 Z', '2015-10-07T00:02:02Z', 1112);
