INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1120, 'Software Developer - Data Visuzalization and Analysis at Esri (Redlands, CA)', '&lt;p&gt;Have you ever wanted to tell a simpler, yet impactful story with your analytic results? Combine your analysis expertise with your eye for design to create outstanding visual representations of data. Join the Analysis and Geoprocessing Team and help us build cutting-edge software and applications that enable ArcGIS users to ask questions, perform analysis, and effectively present their results.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design, develop, and implement data visualization functionality to be used in conjunction with geospatial data across the ArcGIS platform&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Enable ArcGIS users to better understand and communicate analysis results using interactive, informative, and attractive charts and graphs&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Find, analyze, and fix bugs/performance problems whenever and wherever they occur in the software&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build and ship quality software by working effectively with members of other software development teams&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Redlands, CA', 'http://careers.stackoverflow.com/jobs/77679/software-developer-data-visuzalization-and-esri', 'Tue, 06 Oct 2015 23:02:02 Z', '2015-10-06T23:02:02Z', 1120);
