INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1131, 'Software Engineer for Haskell Startup at FP Complete (Chicago, IL) (allows remote)', '&lt;p&gt;To continue growing, we are currently hiring 3 software engineers. You may strongly fit any of the 3 profiles below, and we are also open to a blend of two different descriptions.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;SKILLSET 1: strong at Haskell programming, and will architect and develop server-based infrastructure and data-modeling code, and possibly Web UI code as well.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;SKILLSET 2: strong at devops and cloud Linux sysadmin, and will create and maintain systems for multi-machine computations and for advanced engineering collaboration.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;SKILLSET 3: &amp;nbsp;strong at customer problem analysis and solution design, including writing proposals and specifications, and will play a major role in working with our customers (and their bosses and regulators) and winning new customers for the company.&amp;nbsp;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;You will do many of these task, depending on your skills:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Configure and administer cloud servers running Linux&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Develop components and applications in Haskell&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Analyze Customer needs and write documents describing the problem and the proposed solution&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Assess requirements and pick the best server software to meet each need, including when to develop new software or when to use off-the-shelf software or cloud services&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Install and test Haskell software and libraries as well as non-Haskell software and cloud services&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Develop, test, deploy, and administer Haskell-based web services&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Routine website maintenance and administration&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Collaborate with distributed colleagues via online collaboration tools, email, chat and voice&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;&lt;span&gt;Document technical work so that new colleagues can understand it&lt;/span&gt;&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Chicago, IL', 'http://careers.stackoverflow.com/jobs/99695/software-engineer-for-haskell-startup-fp-complete', 'Tue, 06 Oct 2015 23:02:02 Z', '2015-10-06T23:02:02Z', 1131);
