INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1135, 'Java Software Developer at Wikia Sp. z o.o. (Pozna, Poland)', '&lt;p&gt;&lt;strong&gt;About your role:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br&gt;A Java Developer in the Services Team will be involved in implementing micro services which will provide additional backend functionality in our platform. This is a part of our migration towards SOA effort which includes both implementing new features and creating building blocks for future services.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;A person in this position needs to have a deep understanding of implementing highly scalable and performant web services as well as attention to detail and quality.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Join us!&lt;/p&gt;&lt;br /&gt;&lt;p&gt;As a &lt;strong&gt;Java Software Developer&lt;/strong&gt; at Wikia you will:&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;teach the team of experienced engineers how to take the most advantage of JVM and existing open source solutions&lt;/li&gt;&lt;br /&gt;&lt;li&gt;solve difficult technical and business challenges&lt;/li&gt;&lt;br /&gt;&lt;li&gt;implement new java services, improve existing ones&lt;/li&gt;&lt;br /&gt;&lt;li&gt;create tests and building appropriate base architecture for all Wikia systems&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Pozna, Poland', 'http://careers.stackoverflow.com/jobs/99702/java-software-developer-wikia-sp-z-oo', 'Tue, 06 Oct 2015 23:02:02 Z', '2015-10-06T23:02:02Z', 1129);
