INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1145, 'UI Software Developer - ArcGIS Pro C# at Esri (Redlands, CA)', '&lt;p&gt;Passionate about building great user interfaces that impact the work of hundreds of thousands of users? Join our ArcGIS Pro team and create new, exciting software for Windows from application concept to shipping software. This is a great opportunity to join a fast-paced agile team that works with the latest technologies and patterns including cloud services, asynchronous programming, and MVVM to design and develop next-gen Esri software.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Responsibilities:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Develop and design asynchronous GUI as part of ArcGIS Pro&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Solve and articulate complex problems with application design, development, and user experiences&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Build software that follows industry-standard design patterns, development methodologies, and deployment models&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Collaborate with other programmers and developers throughout the development process&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Redlands, CA', 'http://careers.stackoverflow.com/jobs/77664/ui-software-developer-arcgis-pro-c-sharp-esri', 'Tue, 06 Oct 2015 22:02:02 Z', '2015-10-06T22:02:02Z', 1120);
