INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1148, 'Senior Java Developer at Kliger-Weiss Infosystems, Inc. (Greenvale, NY)', '&lt;p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;JOB DESCRIPTION:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;p&gt;This position is responsible to perform a variety of critical programming assignments requiring knowledge of established programming procedures and data processing requirements, also maintaining and modifying programs.&lt;/p&gt;&lt;br /&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;RESPONSIBILITIES:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;Design and develop systems utilizing various Java technologies including J2EE, JSP, Struts, Servlets and Swing Applets.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Implement web front-ends using HTML, XML, CSS, JavaScript, jQuery, AJAX, JSON and AngularJS.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide system support on legacy UNIX shell scripts&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Perform DB analysis/design on MySQL, including writing complex SQL queries, views, stores procedures, triggers, etc.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Participate in multi-resource projects in an agile, team-driven environment.&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Evaluate/suggest industry standard tools and methods&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide software installation/updates for various environments&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Analyze requirements and provide design/development/test effort estimates as needed&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Document software design and contribute to design reviews&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Provide analysis of available data and work closely with technical and sales teams to guide future projects&lt;/li&gt;&lt;br /&gt;&lt;li&gt;Other duties as assigned&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;', 'Greenvale, NY', 'http://careers.stackoverflow.com/jobs/94963/senior-java-developer-kliger-weiss-infosystems-inc', 'Tue, 06 Oct 20