INSERT INTO OFFER(Id, Title, Description, Location, Url, PubDate, Updated, CompanyId) VALUES (1151, 'Digital Developer - Back End Specialist at Bauer Xcel Media (Bauer Media Group) (Sydney, Australia)', '&lt;p&gt;&lt;strong&gt;BAUER XCEL MEDIA&lt;/strong&gt; is Bauer Media''s Digital Division, advancing the media industry by applying user driven strategies to deliver cutting edge news and build original, innovative products centred around audience needs.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;Work for a large digital media company with offices in New York, Hamburg, London, Sydney, Melbourne, Auckland and Warsaw, you will work on high profile brands with great content to create exciting new digital products in a network that reaches and impacts millions of people.&lt;/p&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;Who you are:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;an experienced .Net developer looking to transition to Node&lt;/li&gt;&lt;br /&gt;&lt;li&gt;understand how to build performant, scalable microservices&lt;/li&gt;&lt;br /&gt;&lt;li&gt;experience working with AWS&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;working knowledge of Continuous Delivery&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;&lt;br /&gt;&lt;p&gt;&lt;strong&gt;As part of the development team you will:&lt;/strong&gt;&lt;/p&gt;&lt;br /&gt;&lt;ul&gt;&lt;br /&gt;&lt;li&gt;build highly robust and scalable API''s&lt;/li&gt;&lt;br /&gt;&lt;li&gt;shape and implement the software architecture and delivery pipeline&amp;nbsp;&lt;/li&gt;&lt;br /&gt;&lt;li&gt;collaborate closely and become a valued contributor in an autonomous cross-functional team&lt;/li&gt;&lt;br /&gt;&lt;li&gt;work in a convenient, lively Sydney CBD location&lt;/li&gt;&lt;br /&gt;&lt;/ul&gt;', 'Sydney, Australia', 'http://careers.stackoverflow.com/jobs/99698/digital-developer-back-end-specialist-bauer-xcel-media-bauer-media', 'Tue, 06 Oct 2015 22:02:02 Z', '2015-10-06T22:02:02Z', 1151);
